INSERT INTO `chapter_dialogues` VALUES (55,26,NULL,'Test',1,'2025-02-28 13:52:56','aside'),(56,27,NULL,'Test',1,'2025-02-28 14:23:04','aside'),(57,28,NULL,'Test',1,'2025-02-28 15:25:34','aside'),(80,31,NULL,'Test',1,'2025-02-28 16:30:18','aside'),(81,32,NULL,'Test',1,'2025-02-28 17:19:06','aside'),(82,33,NULL,'Test',1,'2025-02-28 19:19:57','aside'),(83,34,NULL,'Test',1,'2025-02-28 20:25:26','aside'),(84,35,NULL,'Test',1,'2025-02-28 21:21:33','aside'),(85,36,NULL,'Test',1,'2025-02-28 22:22:39','aside'),(86,37,NULL,'Test',1,'2025-02-28 23:21:51','aside'),(87,38,NULL,'Test',1,'2025-03-01 01:58:31','aside'),(88,39,NULL,'Test',1,'2025-03-01 03:02:34','aside'),(89,40,NULL,'Test',1,'2025-03-01 04:27:20','aside'),(90,41,NULL,'Test',1,'2025-03-01 05:22:22','aside'),(91,42,NULL,'Test',1,'2025-03-01 06:32:14','aside'),(92,43,NULL,'Test',1,'2025-03-01 07:20:51','aside'),(93,44,NULL,'Test',1,'2025-03-01 08:28:13','aside'),(94,45,NULL,'Test',1,'2025-03-01 09:22:33','aside'),(95,46,NULL,'Test',1,'2025-03-01 10:23:46','aside'),(96,47,NULL,'Test',1,'2025-03-01 11:17:44','aside'),(97,48,NULL,'Test',1,'2025-03-01 12:42:37','aside'),(98,49,NULL,'Test',1,'2025-03-01 13:24:43','aside'),(99,50,NULL,'Test',1,'2025-03-01 14:21:16','aside'),(100,51,NULL,'Test',1,'2025-03-01 15:23:11','aside'),(101,52,NULL,'Test',1,'2025-03-01 16:26:40','aside'),(102,53,NULL,'Test',1,'2025-03-01 17:19:09','aside'),(103,54,NULL,'Test',1,'2025-03-01 18:29:36','aside'),(104,55,NULL,'Test',1,'2025-03-01 19:17:04','aside'),(105,56,NULL,'Test',1,'2025-03-01 20:23:22','aside'),(106,57,NULL,'Test',1,'2025-03-01 21:21:08','aside'),(107,58,NULL,'Test',1,'2025-03-01 22:21:38','aside'),(108,59,NULL,'Test',1,'2025-03-01 23:22:25','aside'),(109,60,NULL,'Test',1,'2025-03-02 01:57:27','aside'),(110,61,NULL,'Test',1,'2025-03-02 03:00:09','aside'),(111,62,NULL,'Test',1,'2025-03-02 03:34:31','aside'),(112,63,NULL,'Test',1,'2025-03-02 04:26:30','aside'),(113,64,NULL,'Test',1,'2025-03-02 05:24:11','aside'),(114,65,NULL,'Test',1,'2025-03-02 06:31:40','aside'),(115,66,NULL,'Test',1,'2025-03-02 07:20:20','aside'),(116,67,NULL,'Test',1,'2025-03-02 08:27:01','aside'),(117,68,NULL,'Test',1,'2025-03-02 09:23:36','aside'),(118,69,NULL,'Test',1,'2025-03-02 10:24:01','aside'),(119,70,NULL,'Test',1,'2025-03-02 11:18:08','aside'),(120,71,NULL,'Test',1,'2025-03-02 12:41:36','aside'),(121,72,NULL,'Test',1,'2025-03-02 13:24:05','aside'),(122,73,NULL,'Test',1,'2025-03-02 14:21:11','aside'),(123,74,NULL,'Test',1,'2025-03-02 15:23:31','aside'),(124,75,NULL,'Test',1,'2025-03-02 16:27:13','aside'),(125,76,NULL,'Test',1,'2025-03-02 17:19:07','aside'),(126,77,NULL,'Test',1,'2025-03-02 18:30:59','aside'),(127,78,NULL,'Test',1,'2025-03-02 19:17:09','aside'),(128,79,NULL,'Test',1,'2025-03-02 20:23:34','aside'),(129,80,NULL,'Test',1,'2025-03-02 21:21:04','aside'),(130,81,NULL,'Test',1,'2025-03-02 22:22:08','aside'),(131,82,NULL,'Test',1,'2025-03-02 23:21:38','aside'),(132,83,NULL,'Test',1,'2025-03-03 01:55:18','aside'),(133,84,NULL,'Test',1,'2025-03-03 02:59:12','aside'),(270,91,NULL,'Hikaru dụi mắt, lồm cồm bò dậy sau một đêm dài cày game. Ánh sáng yếu ớt lọt qua khe cửa sổ, chiếu lên đống vỏ mì tôm và manga ngổn ngang trên sàn.',1,'2025-03-04 15:27:39','aside'),(272,91,NULL,'Cậu lảo đảo bước đến cửa sổ, kéo mạnh tấm rèm đã sờn cũ. Một cảnh tượng kinh hoàng đập vào mắt cậu.',3,'2025-03-04 15:27:45','aside'),(273,91,8,'Ơ... Cái quái gì thế này?',4,'2025-03-04 15:27:46','dialogue'),(275,91,8,'(Lùi lại, lắp bắp) Z... Zombie? Không thể nào... Mình đang mơ à?',6,'2025-03-04 15:27:49','dialogue'),(276,91,NULL,'Hikaru vội vã bật TV. Bản tin thời sự đang phát đi những hình ảnh hỗn loạn, với dòng chữ chạy ngang màn hình: Đại dịch zombie bùng phát trên toàn cầu!',7,'2025-03-04 15:27:50','aside'),(277,91,8,'(Tuyệt vọng) Không... Đây không phải là mơ! Mình tiêu thật rồi!',8,'2025-03-04 15:27:51','dialogue'),(278,91,NULL,'Bỗng nhiên, một ý nghĩ lóe lên trong đầu Hikaru.',9,'2025-03-04 15:27:53','aside'),(279,91,8,'(Lẩm bẩm) Khoan đã... Mình đã đọc cả ngàn cuốn manga về zombie, xem không biết bao nhiêu phim kinh dị... Mình biết cách sống sót!',10,'2025-03-04 15:27:54','dialogue');
